const int INF = 1e9 + 5;

class Solution {
public:
    vector<int> smallestSufficientTeam(vector<string>& req_skills, vector<vector<string>>& people) {
        int s = req_skills.size();
        int n = people.size();
        vector<int> masks(n, 0);

        for (int i = 0; i < n; i++)
            for (string skill : people[i]) {
                int index = -1;

                for (int j = 0; j < s; j++)
                    if (skill == req_skills[j])
                        index = j;

                if (index >= 0)
                    masks[i] |= 1 << index;
            }

        vector<vector<int>> dp(n + 1, vector<int>(1 << s, INF));
        dp[0][0] = 0;

        for (int i = 0; i < n; i++)
            for (int mask = 0; mask < 1 << s; mask++) {
                dp[i + 1][mask] = min(dp[i + 1][mask], dp[i][mask]);
                dp[i + 1][mask | masks[i]] = min(dp[i + 1][mask | masks[i]], dp[i][mask] + 1);
            }

        int current = (1 << s) - 1;
        assert(dp[n][current] < INF);
        vector<int> team;

        for (int i = n - 1; i >= 0; i--) {
            int next_mask = -1;

            for (int m = 0; m < 1 << s; m++)
                if ((current | m) == current && (m | masks[i]) == current && dp[i][m] + 1 == dp[i + 1][current]) {
                    next_mask = m;
                    break;
                }

            if (next_mask >= 0) {
                team.push_back(i);
                current = next_mask;
            }
        }

        reverse(team.begin(), team.end());
        return team;
    }
};

class Solution:
    def smallestSufficientTeam(self, req_skills: List[str], people: List[List[str]]) -> List[int]:
        people = [(lst, i) for i, lst in enumerate(people)]
        people.sort(key=lambda x : len(x[0]), reverse=True)
        reqs = {skill : i for i, skill in enumerate(req_skills)}
        n = len(reqs)
        nstates = (1 << n)
        dp = [list(range(len(people))) for state in range(nstates)]
        dp[0] = []
        for lst, i in people:
            skill = 0
            for item in lst:
                idx = reqs.get(item, None)
                if idx is not None:
                    skill |= (1 << idx)
            for key, value in enumerate(dp):
                new = (skill | key)
                if new != key and len(dp[new]) > len(value) + 1:
                    dp[new] = value + [i]
        return sorted(dp[nstates - 1])

