var smallestSufficientTeam = function(req_skills, people) {
    let dig = req_skills.length;
    let goal = (1<<dig)-1;
    let hash = new Map();
    for(let i=0;i<req_skills.length;i++){
        let skill=req_skills[i];
        let val = 1<<i;
        hash.set(skill,val);
    }
    people = people.map((skills,index)=>[index,skills.reduce((b,skill)=>(b|hash.get(skill)),0)]);
    let stack=[[0,[]]];
    let visited = new Set();
    while(stack.length>0){
        let [state,preArr] = stack.shift();
        if(state===goal){
            return preArr;
        }
        for(let [index,personState] of people){

            let nextState = (state|personState);
            if(!visited.has(nextState)){
                visited.add(nextState);
                stack.push([nextState,preArr.concat([index])]);
            }
        }
    }
};

