from functools import lru_cache
class Solution:
    def shortestSuperstring(self, A):
        """
        :type A: List[str]
        :rtype: str
        """
        A.sort(key=len)
        B = []
        for s in A:
            if not any(s in s1 for s1 in B):
                B.append(s)
        @lru_cache(None)
        def overlap(s1, s2):
            return max((i for i in range(1, len(s2)) if s2[:i] == s1[-i:]), default=0)
        @lru_cache(None)
        def get(to_use, first):
            if to_use == 1 << first:
                return A[first]
            best = ''.join(A)
            for l1 in range(len(A)):
                if (to_use & (1 << l1)) and l1 != first:
                    r = get(to_use ^ (1 << first), l1)
                    r = A[first][:len(A[first]) - overlap(A[first], A[l1])] + r
                    if len(r) < len(best):
                        best = r
            return best
        return min((get((1 << len(A)) - 1, i) for i in range(len(A))), key=len)

