class Solution:
    def smallestNumber(self, pattern: str) -> str:
        def solve(p, digits):
            if not p:
                return min(digits)
            for d in sorted(digits):
                rem = digits[:]
                rem.remove(d)
                rest = solve(p[1:], rem)
                if p[0] == 'I':
                    if d < rest[0]:
                        return d + rest
                else:
                    if d > rest[0]:
                        return d + rest

        return solve(pattern, list('123456789'))

class Solution {

	public String smallestNumber(String pattern) {
		for (int i = 1;; i++) {
			boolean[] visited = new boolean[10];
			visited[i] = true;
			String s = smallestNumber(0, i, pattern, visited);
			if (s != null) {
				return i + s;
			}
		}
	}

	private String smallestNumber(int index, int prev, String pattern, boolean[] visited) {
		if (index == pattern.length()) {
			return "";
		}
		for (int i = prev; i > 0 && i < 10; i += pattern.charAt(index) == 'I' ? 1 : -1) {
			if (!visited[i]) {
				visited[i] = true;
				String s = smallestNumber(index + 1, i, pattern, visited);
				if (s != null) {
					return i + s;
				}
				visited[i] = false;
			}
		}
		return null;
	}
}

