            return;
        }
        for (let i = 1; i <= 9; i ++ ) {
            if (st[i]) continue;
            st[i] = true;
            path.push(i);
            dfs(u + 1);
            path.pop();
            st[i] = false;
        }
    }
    dfs(0);
    res.sort();
    // console.log(res);
    return res[0]; 
};

impl Solution {
    pub fn smallest_number(pattern: String) -> String {
        fn backtrack(digits: &mut Vec<i32>, pattern: &[u8], curr: usize, used: i32) -> bool {
            if curr == pattern.len() {
                return true;
            }
            let prev = digits[digits.len() - 1];
            if pattern[curr] == b'I' {
                for next in (prev + 1)..=9 {
                    if used & (1 << next) == 0 {
                        digits.push(next);
                        if backtrack(digits, pattern, curr + 1, used | (1 << next)) {
                            return true;
                        }
                        digits.pop();
                    }
                }
                false
            } else {
                for next in 1..prev {
                    if used & (1 << next) == 0 {
                        digits.push(next);
                        if backtrack(digits, pattern, curr + 1, used | (1 << next)) {
                            return true;
                        }
                        digits.pop();
                    }
                }
                false
            }
        }

        let n = pattern.len();
        let pb = pattern.as_bytes();
        let mut digits = Vec::with_capacity(n + 1);
        for first in 1..=9 {
            digits.push(first);
            if backtrack(&mut digits, pb, 0, 1 << first) {
                break;
            }
            digits.pop();
        }

        let mut ret = String::new();
        for d in digits {
            ret += &d.to_string();
        }
        ret
    }
}

