class Solution {
public:
    TreeNode* canMerge(vector<TreeNode*>& trees) {
        map<int, TreeNode*> leaf;
        int n = 0;
        int ok = 1;
        function<void(TreeNode*, int)> dfs1 = [&](TreeNode* p, int r) {
            n += 1;
            if (not p->left and not p->right and r == 0) {
                if (leaf.count(p->val)) ok = 0;
                leaf[p->val] = p;
            }
            if (p->left) dfs1(p->left, 0);
            if (p->right) dfs1(p->right, 0);
        };
        for (auto p : trees)
            dfs1(p, 1);
        if (not ok) return nullptr;
        TreeNode* root = nullptr;
        for (auto p: trees)
            if (not leaf.count(p->val)) {
                if (root != nullptr) ok = 0;
                root = p;
            }
        if (not ok or root == nullptr) return nullptr;
        for (auto p : trees) if (p != root) {
            auto x = leaf[p->val];
            x->left = p->left;
            x->right = p->right;
        }
        int m = 0;
        set<TreeNode*> s;
        function<pair<int, int>(TreeNode*)> dfs2 = [&](TreeNode* p) {
            pair<int, int> res = {p->val, p->val};
            if (ok == 0 or s.count(p)) {
                ok = 0;
                return res;
            }
            m += 1;
            if (p->left) {
                auto L = dfs2(p->left);
                if (p->val <= L.second) ok = 0;
                res.first = L.first;
            }
            if (p->right) {
                auto R = dfs2(p->right);
                if (p->val >= R.first) ok = 0;
                res.second = R.second;
            }
            return res;
        };
        dfs2(root);
        if (not ok or n != m + (int)trees.size() - 1) return nullptr;
        return root;
    }
};

/**
 * Definition for a binary tree node.
 * function TreeNode(val, left, right) {
 *     this.val = (val===undefined ? 0 : val)
 *     this.left = (left===undefined ? null : left)
 *     this.right = (right===undefined ? null : right)
 * }
 */
/**
 * @param {TreeNode[]} trees
 * @return {TreeNode}
 */
var canMerge = function(trees) {
    trees = new Map(trees.map(x => [x.val, x]))
    let leaves = getLeaves(trees)
    let root
    for (let [key, tree] of trees) {
        if (!leaves.has(key)) root = tree
    }
    // console.log('leaves', leaves)
    // console.log('root', root)
    if (!root) return null
    trees.delete(root.val)

    construct(root, trees)
    // console.log('constructed', root)
    // console.log('remain', trees)
    if (trees.size) return null
    if (valid(root, -Infinity, Infinity)) return root
    return null
};
function valid(root, min, max) {
    if (!root) return true
    if (root.val > min && root.val < max) {
        return valid(root.left, min, root.val)
            && valid(root.right, root.val, max)
    }
    return false
}
function construct(root, trees) {
    if (root.left) {
        let lval = root.left.val
        if (trees.has(lval)) {
            root.left = trees.get(lval)
            trees.delete(lval)
            construct(root.left, trees)
        }
    }
    if (root.right) {
        let rval = root.right.val
        if (trees.has(rval)) {
            root.right = trees.get(rval)
            trees.delete(rval)
            construct(root.right, trees)
        }
    }
}
function getLeaves(trees) {
    let ans = new Set()
    for (let tree of trees.values()) {
        if (tree.left) ans.add(tree.left.val)
        if (tree.right) ans.add(tree.right.val)
    }
    return ans
}

