class Solution:
    def maxScore(self, nums1: List[int], nums2: List[int], k: int) -> int:
        p = [[a, b] for (a, b) in zip(nums1, nums2)]
        p.sort(key=lambda x: x[1], reverse=True)
        stl = SortedList()
        res = 0
        cur = p[0][1]
        curs = 0
        for a, b in p:
            cur = min(cur, b)
            if len(stl) < k:
                curs += a
            else:
                u = stl[-k]
                if a > u:
                    curs += a - u
            stl.add(a)
            if len(stl) >= k:
                res = max(res, cur * curs)
        return res

class Solution {
	public long maxScore(int[] nums1, int[] nums2, int k) {
		int n = nums1.length;
		int[][] a = new int[n][];
		for(int i = 0;i < n;i++)a[i] = new int[]{nums2[i], nums1[i]};
		Arrays.sort(a, (x, y) -> {
			if (x[0] != y[0]) return x[0] - y[0];
			return (x[1] - y[1]);
		});
		PriorityQueue<Integer> pq = new PriorityQueue<>();
		long ans = 0;
		long s = 0;
		for(int i = n-1;i >= 0;i--){
			pq.add(a[i][1]);
			s += a[i][1];
			if(pq.size() > k)s -= pq.poll();
			if(pq.size() == k){
				ans = Math.max(ans, s * a[i][0]);
			}
		}
		return ans;
	}
}

class Solution {
public:
    long long maxScore(vector<int>& nums1, vector<int>& nums2, int k) {
        int n = nums1.size(); 
        vector<pair<int, int>> two; 
        for (int i = 0; i < n; ++i) 
            two.emplace_back(nums2[i], nums1[i]); 
        sort(two.begin(), two.end(), greater<>()); 
        for (int i = 0; i < n; ++i) {
            nums1[i] = two[i].second; 
            nums2[i] = two[i].first; 
        }
        priority_queue<int, vector<int>, greater<>> pq; 
        long long ans = 0, sum = 0; 
        for (int i = 0; i < n; ++i) {
            sum += nums1[i]; 
            pq.push(nums1[i]); 
            if (i >= k) {
                sum -= pq.top(); pq.pop(); 
            }
            if (i >= k-1) ans = max(ans, sum * nums2[i]); 
        }
        return ans; 
    }
};

