impl Solution {
    pub fn max_score(nums1: Vec<i32>, nums2: Vec<i32>, k: i32) -> i64 {
        use std::collections::BTreeMap;
        let n = nums1.len();
        let k = k as usize;
        let mut dic2 = BTreeMap::new();
        let mut dic1 = BTreeMap::new();
        for i in 0..n {
            (*dic2.entry(nums2[i]).or_insert(Vec::<usize>::new())).push(i);
        }
        let mut ans = 0i64;
        let mut total = 0i64;
        let mut count = 0usize;
        for (key, val) in dic2.iter().rev() {
            for i in val {
                if count < k {
                    *dic1.entry(nums1[*i]).or_insert(0) += 1;
                    total += nums1[*i] as i64;
                    count += 1;
                } else {
                    let fkey = *dic1.iter().next().unwrap().0;
                    if fkey < nums1[*i] {
                        *dic1.entry(nums1[*i]).or_insert(0) += 1;
                        total += nums1[*i] as i64;
                        total -= fkey as i64;
                        *dic1.entry(fkey).or_insert(0) -= 1;
                        if dic1[&fkey] == 0 {
                            dic1.remove(&fkey);
                        }
                    }
                }
            }
            if count == k {
                ans = ans.max(total * *key as i64);
            }
        }
        ans
    }
}

/**
 * @param {number[]} nums1
 * @param {number[]} nums2
 * @param {number} k
 * @return {number}
 */
var maxScore = function(nums1, nums2, k) {
    const n = nums1.length;
    const maxPQ = new MaxPriorityQueue({compare: (a, b) => b.num - a.num});
    nums2.forEach((num, index) => maxPQ.enqueue({num, index}));

    const minPQ = new MinPriorityQueue({compare: (a, b) => a - b});

    let sum = 0;
    for (let i = 0; i < k - 1; i++) {
        const { index } = maxPQ.dequeue();
        const val = nums1[index];
        minPQ.enqueue(val);
        sum += val;
    }

    const { num, index } = maxPQ.dequeue();
    const val = nums1[index];
    minPQ.enqueue(val);
    sum += val;

    let ans = num * sum;

    for (let i = k; i < n; i++) {
        const item = maxPQ.dequeue();
        const val1 = nums1[item.index];
        const val2 = minPQ.dequeue();
        if (val1 <= val2) {
            minPQ.enqueue(val2);
            continue;
        }

        sum += val1 - val2;
        ans = Math.max(sum * item.num, ans);
        minPQ.enqueue(val1);
    }

    return ans;
};

