	int[] ans;

	void dfs(int cur, int pre, int[][] g, TrieArrayBinaryWithNum trie, int[][][] B)
	{
		trie.add(cur);
		for(int[] q : B[cur]){
			ans[q[2]] = (int)trie.xormax(q[1]);
		}
		for(int e : g[cur]){
			if(e == pre)continue;
			dfs(e, cur, g, trie, B);
