	public int[][][] makeBuckets(int[][] a, int sup)
	{
		int n = a.length;
		int[][][] bucket = new int[sup+1][][];
		int[] bp = new int[sup+1];
		for(int i = 0;i < n;i++)bp[a[i][0]]++;
		for(int i = 0;i <= sup;i++)bucket[i] = new int[bp[i]][];
		for(int i = n-1;i >= 0;i--)bucket[a[i][0]][--bp[a[i][0]]] = a[i];
		return bucket;
	}


	public class L {
		public int[] a;
		public int p = 0;

		public L(int n) { a = new int[n]; }
		public L add(int n)
		{
			if(p >= a.length)a = Arrays.copyOf(a, a.length*3/2+1);
			a[p++] = n;
			return this;
		}
		public int size() { return p; }
		public L clear() { p = 0; return this; }
		public int[] toArray() { return Arrays.copyOf(a, p); }
		@Override
		public String toString() {return Arrays.toString(toArray());}
	}


	public class TrieArrayBinaryWithNum {
		// root = 0
		public L next;
		public int gen;
		public int W;
		public L num;

		public TrieArrayBinaryWithNum(int W) {
			this.W = W;
			this.next = new L(2);
			this.gen = 1;
			this.next.add(-1).add(-1);
			this.num = new L(2);
			this.num.add(0);
		}

		public void add(long s) {
			add(s, 1);
		}

		public void add(long s, int x) {
			int cur = 0;
			for (int d = W - 1; d >= 0; d--) {
				num.a[cur] += x;
				int v = (int) (s >>> d & 1);
				int nex = next.a[cur * 2 + v];
				if (nex == -1) {
					nex = next.a[cur * 2 + v] = gen++;
					next.add(-1).add(-1);
					num.add(0);
				}
				cur = nex;
			}
			num.a[cur] += x;
		}

		public long xormax(long x)
		{
			if(gen == 1)return 0;
			int cur = 0;
			long ret = 0;
			for(int d = W-1;d >= 0;d--){
				if(cur == -1 || num.a[cur] == 0){
					ret |= x<<-d>>>-d;
					break;
				}
				int xd = (int)(x>>>d&1);
				if(next.a[cur*2|xd^1] != -1 && num.a[next.a[cur*2|xd^1]] > 0){
					ret |= 1L<<d;
					cur = next.a[cur*2|xd^1];
				}else{
					cur = next.a[cur*2|xd];
				}
			}
			return ret;
		}
	}


		public int[][] parentToG(int[] par)
	{
		int n = par.length;
		int[] ct = new int[n];
		for(int i = 0;i < n;i++){
			if(par[i] >= 0){
				ct[i]++;
				ct[par[i]]++;
			}
		}
		int[][] g = new int[n][];
		for(int i = 0;i < n;i++){
			g[i] = new int[ct[i]];
		}
		for(int i = 0;i < n;i++){
			if(par[i] >= 0){
				g[par[i]][--ct[par[i]]] = i;
				g[i][--ct[i]] = par[i];
			}
		}
		return g;
	}

}

class Solution:
    def maxGeneticDifference(self, parents: List[int], queries: List[List[int]]) -> List[int]:
        n = len(parents)
        G = [[] for _ in range(n)]
        for i, p in enumerate(parents):
            if p == -1: root = i
            else: G[p].append(i)
        m = len(queries)
        ans = [-1] * m
        for i in range(m): queries[i].append(i)
        dic = defaultdict(list)
        for node, val, i in queries:
            dic[node].append((val, i))
        T = lambda: defaultdict(T)
        trie = T()

        def dfs(i):
            nonlocal trie
            cur = trie
            for k in range(17, -1, -1):
                b = i >> k & 1
                if '#' not in cur:
                    cur['#'] = [0, 0]
                cur['#'][b] += 1
                cur = cur[b]
            for val, idx in dic[i]:
                tmp = 0
                cur = trie
                for k in range(17, -1, -1):
                    b = val >> k & 1
                    if cur['#'][1 - b]:
                        tmp |= 1 << k
                        cur = cur[1 - b]
                    else:
                        cur = cur[b]
                ans[idx] = tmp
            for j in G[i]:
                dfs(j)
            cur = trie
            for k in range(17, -1, -1):
                b = i >> k & 1
                cur['#'][b] -= 1
                cur = cur[b]

        dfs(root)
        return ans

class Solution {
public:
    int M=1000000007;
    long long quickpow(long long a,long long b){
        long long k=1;
        while(b>0){
            if(b&1){
                k=(k*a)%M;
            }
            b=b>>1;
            a=(a*a)%M;
        }
        return k;
    }
    int numSubseq(vector<int>& nums, int target) {
        sort(nums.begin(),nums.end());
        int i=0;
        int j=nums.size()-1;
        long long res=0;
        while(i<=j){
            while(i<=j&&nums[i]+nums[j]>target){
                --j;
            }
            if(i>j)
                break;
            res+=quickpow(2,j-i);
            res%=M;
            ++i;
        }
        return res;
    }
};

