    public int numSubseq(int[] a, int target) {
        int n = a.length;
        Integer[] indices = new Integer[n];
        for (int i = 0; i < n; i++) {
            indices[i] = i;
        }
        Arrays.sort(indices, 0, n, (x, y) -> a[x] == a[y] ? Integer.compare(x, y) : Integer.compare(a[x], a[y]));
        int r = 0;
        int[] pow2 = new int[n + 1];
        pow2[0] = 1;
        for (int i = 1; i <= n; i++) {
            pow2[i] = pow2[i - 1] * 2 % mod;
        }
        int ans = 0;
        //System.out.println(Arrays.deepToString(indices));
        r = n - 1;
        for (int i = 0; i < n; i++) {
            r = Math.max(r, i);
            while (r - 1 >= i && a[indices[r]] + a[indices[i]] > target) {
                r--;
            }
           // System.out.println(indices[i] + ":" + r);
            if (a[indices[r]] + a[indices[i]] <= target) {
                int cnt = r - i;
                ans = (ans + pow2[cnt]) % mod;
            }

        }

        return ans;
    }

    int mod = (int) (1e9 + 7);
}

class Solution(object):
    def numSubseq(self, A, K):
        MOD = 10 ** 9 + 7
        N = len(A)
        pow2 = [1]
        for x in xrange(1, N + 1):
            pow2.append(pow2[-1] * 2 % MOD)
        A.sort()

        ans = 0
        j = N - 1
        for i in range(N):
            while j and A[i] + A[j] > K:
                j -= 1
            if i <= j and A[i] + A[j] <= K:
                ans += pow2[j - i]
                ans %= MOD
        return ans

/**
 * @param {number[]} nums
 * @param {number} target
 * @return {number}
 */
var numSubseq = function(nums, target) {
    let N = nums.length
    nums.sort((l, r) => l - r)
    let lo = 0, hi = N - 1
    let ans = 0
    let MOD = 1e9 + 7
    let MODn = BigInt(1e9 + 7)
    while (lo <= hi) {
        if (nums[lo] + nums[hi] > target) hi--
        else {
            ans += Number(pow(2n, BigInt(hi - lo)))
            ans %= MOD
            lo++
        }
    }
    return ans
    function pow(base, p) {
        if (p === 0n) return 1n
        if (p === 1n) return base
        if (p % 2n === 1n) return pow(base, p - 1n) * base % MODn
        let half = pow(base, p / 2n)
        return half * half % MODn
    }
};

impl Solution {
  pub fn num_subseq(mut nums: Vec<i32>, target: i32) -> i32 {
    let mut power2 = vec![1];
    const M: i64 = 1e9 as i64 + 7;
    for i in 1..=nums.len() {
      power2.push(power2[i-1] * 2 % M);
    }

    nums.sort_unstable();
    let mut ans = 0;
    let mut l = 0;
    for r in (0..nums.len()).rev() {
      while l < nums.len() && nums[l] + nums[r] <= target {
        l += 1;
      }
      use std::cmp::max;
      // if we have nums[r], the left one can be nums[0..l),
      // calculate 2^(r-l) + ... + 2^(r-1)
      // (2^l - 1) * 2^(r-l)
      let new_ones = if r >= l {
        (power2[l] - 1) * power2[r - l]
      } else {
        power2[r]
      };
      // println!("{:?}, {}", (l, r), new_ones);
      ans += new_ones % M;
      ans %= M;
    }
    ans as i32
  }
}

