class Solution {
public:
    int uniquePathsIII(vector<vector<int>>& grid) {
		int H=grid.size();
		int W=grid[0].size();
		int y,x;
		int sx,sy,gx,gy;
		int sm=0;
		FOR(y,H) FOR(x,W) {
			if(grid[y][x]==1) sx=x,sy=y,sm|=1<<(y*W+x);
			if(grid[y][x]==2) gx=x,gy=y;
			if(grid[y][x]==-1) sm|=1<<(y*W+x);
		}
        ZERO(dp);

        dp[sm][sy*W+sx]=1;
        int mask;
        FOR(mask,1<<(H*W)) {
			int cx,cy,i;
			FOR(cy,H) FOR(cx,W) if(dp[mask][cy*W+cx]) {
				int d[4]={0,1,-1,0};
				FOR(i,4) {
					int ty=cy+d[i];
					int tx=cx+d[i^1];
					if(ty<0||ty>=H||tx<0||tx>=W) continue;
					if(mask&(1<<(ty*W+tx))) continue;
					dp[mask|(1<<(ty*W+tx))][ty*W+tx]+=dp[mask][cy*W+cx];
				}

			}
		}

        return dp[(1<<(H*W))-1][gy*W+gx];

    }
};

class Solution {
    static boolean[][] vis;
    static int[][] grid;
    static int[] dx = {1, 0, -1, 0};
    static int[] dy = {0, -1, 0, 1};

    public int dfs(int x, int y, int ex, int ey, int open) {
        if (x < 0 || x >= grid.length || y < 0 || y >= grid[x].length || vis[x][y] || grid[x][y] == -1) {
            return 0;
        }

        if (x == ex && y == ey) {
            return open == 1 ? 1 : 0;
        }
        int ans = 0;
        vis[x][y] = true;
        for (int d = 0; d < 4; ++d) {
            ans += dfs(x + dx[d], y + dy[d], ex, ey, open - 1);
        }
        vis[x][y] = false;
        return ans;
    }

    public int uniquePathsIII(int[][] _grid) {
        grid = _grid;
        int n = grid.length;
        int m = grid[0].length;
        vis = new boolean[n][m];
        int open = 0;
        int sx = -1;
        int sy = -1;
        int ex = -1;
        int ey = -1;
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < m; ++j) {
                if (grid[i][j] == 1) {
                    sx = i;
                    sy = j;
                    grid[i][j] = 0;
                } else if (grid[i][j] == 2) {
                    ex = i;
                    ey = j;
                    grid[i][j] = 0;
                }

                if (grid[i][j] == 0) {
                    open++;
                }
            }
        }

        return dfs(sx, sy, ex, ey, open);
    }
}

