/**
 * @param {number[][]} grid
 * @return {number}
 */
var uniquePathsIII = function(grid) {
    const r = grid.length, c = grid[0].length;
    const visited = Array.from(Array(r), () => Array(c));
    let visitedn = 0;
    let aim = r*c;
    let i0, j0;
    for (let i = 0; i < r; ++i) {
        const row = grid[i];
        for (let j = 0; j < c; ++j) {
            const a = row[j];
            if (a === 1) {
                i0 = i;
                j0 = j;
            } else if (a === -1) {
                --aim;
            }
        }
    }
    const find = (i = i0, j = j0) => {
        if (i < 0 || i >= r || j < 0 || j >= c) {
            return 0;
        }
        const a = grid[i][j];
        if (a === -1) {
            return 0;
        } else if (a === 2) {
            return visitedn + 1 === aim;
        }
        if (visited[i][j]) {
            return 0;
        }
        visited[i][j] = true;
        ++visitedn;
        const n = find(i+1, j) + find(i-1, j) + find(i, j+1) + find(i, j-1);
        visited[i][j] = false;
        --visitedn;
        return n;
    };
    return find();
};

