            for(int j=0;j<m;++j) {
                if(vis[i][j] || !b[i][j]) continue;
                queue<pair<int,int>> q;
                q.push({i,j});
                vis[i][j]=1;
                int ok=1;
                while(!q.empty()) {
                    auto cur=q.front();q.pop();
                    int x=cur.first,y=cur.second;
                    if(!a[x][y]) ok=0;
                    for(int k=0;k<4;++k) {
