class Solution {
	public int countSubIslands(int[][] grid1, int[][] grid2) {
		int n = grid1.length, m = grid1[0].length;
		DJSet ds1 = new DJSet(n*m);
		DJSet ds2 = new DJSet(n*m);
		for(int i = 0;i < n;i++){
			for(int j = 0;j < m;j++){
				if(i+1 < n && grid1[i][j] == 1 && grid1[i+1][j] == 1){
					ds1.union(i*m+j, (i+1)*m+j);
				}
				if(j+1 < m && grid1[i][j] == 1 && grid1[i][j+1] == 1){
					ds1.union(i*m+j, i*m+j+1);
				}
			}
		}
		for(int i = 0;i < n;i++){
			for(int j = 0;j < m;j++){
				if(i+1 < n && grid2[i][j] == 1 && grid2[i+1][j] == 1){
					ds2.union(i*m+j, (i+1)*m+j);
				}
				if(j+1 < m && grid2[i][j] == 1 && grid2[i][j+1] == 1){
					ds2.union(i*m+j, i*m+j+1);
				}
			}
		}

		int[] dom = new int[n*m];
		Arrays.fill(dom, -1);
		for(int i = 0;i < n;i++){
			for(int j = 0;j < m;j++){
				if(grid2[i][j] == 0)continue;
				int root = ds2.root(i*m+j);
				if(grid1[i][j] == 0 || dom[root] == -2){
					dom[root] = -2;
					continue;
				}
				int r1 = ds1.root(i*m+j);
				if(dom[root] == -1){
					dom[root] = r1;
				}else if(dom[root] != r1){
					dom[root] = -2;
				}
			}
		}
		int ans = 0;
		for(int i = 0;i < n;i++){
			for(int j = 0;j < m;j++){
				if(dom[i*m+j] >= 0)ans++;
			}
		}
		return ans;
	}

	public class DJSet {
		public int[] upper;

		public DJSet(int n) {
			upper = new int[n];
			Arrays.fill(upper, -1);
		}

		public int root(int x) {
			return upper[x] < 0 ? x : (upper[x] = root(upper[x]));
		}

		public boolean equiv(int x, int y) {
			return root(x) == root(y);
		}

		public boolean union(int x, int y) {
			x = root(x);
			y = root(y);
			if (x != y) {
				if (upper[y] < upper[x]) {
					int d = x;
					x = y;
					y = d;
				}
				upper[x] += upper[y];
				upper[y] = x;
			}
			return x == y;
		}

		public int count() {
			int ct = 0;
			for (int u : upper) if (u < 0) ct++;
			return ct;
		}

		public int[][] toBucket() {
			int n = upper.length;
			int[][] ret = new int[n][];
			int[] rp = new int[n];
			for (int i = 0; i < n; i++) if (upper[i] < 0) ret[i] = new int[-upper[i]];
			for (int i = 0; i < n; i++) {
				int r = root(i);
				ret[r][rp[r]++] = i;
			}
			return ret;
		}
	}

}

