class Solution {
public:
    int findRotateSteps(string ring, string key) {
        int m = ring.size(), n = key.size();
        vector<vector<int>> dp;
        vector<int> ss; ss.resize(m);
        dp.resize(n + 1, ss);
        dp[0][0] = 0;
        for(int j = 1; j < m; j++)
            dp[0][j] = 23333333;
        int ans = 23333333;
        for(int i = 1; i <= n; i++)
            for(int j = 0; j < m; j++)
            {
                if(ring[j] != key[i - 1]) dp[i][j] = 23333333;
                else
                {
                    dp[i][j] = 23333333;
                    for(int k = 0; k < m; k++) dp[i][j] = min(dp[i][j], dp[i - 1][k] + min(min(abs(k - j), abs(k + m - j)), abs(j + m - k)));
                }
                if(i == n) ans = min(ans, dp[i][j]);
            }
        return ans + n;
    }
};

public class Solution {
    public int findRotateSteps(String ring, String key) {
        int len=key.length();
        int n=ring.length();
        int[][] dp=new int[n][len];
        ArrayList<Integer>[] lists=new ArrayList[26];
        for (int i=0;i<26;i++) lists[i]=new ArrayList<>();
        for (int i=0;i<n;i++)
            lists[ring.charAt(i)-'a'].add(i);
        for (int i=len-1;i>=0;i--) {
            char c=key.charAt(i);
            if (i==len-1) {
                for (int l: lists[c-'a'])
                    dp[l][i]=1;
            }
            else {
                char c2=key.charAt(i+1);
                for (int l: lists[c-'a']) {
                    dp[l][i]=999999999;
                    for (int w: lists[c2-'a']) {
                        dp[l][i]=Math.min(dp[l][i], dp[w][i+1]+distance(l,w,n)+1);
                    }
                }
            }
        }
        int ans=Integer.MAX_VALUE;
        char c=key.charAt(0);
        for (int l: lists[c-'a'])
            ans=Math.min(ans, distance(0, l, n)+dp[l][0]);
        return ans;

    }
    private int distance(int i, int j, int n) {
        return Math.min(Math.abs(i-j), i<=j?Math.abs(i+n-j):Math.abs(j+n-i));
    }
}

class Solution(object):
    def findRotateSteps(self, ring, key):
        def get(i):
            return ring[i % len(ring)]
        memo = {}
        def dp(pos, k):
            if (pos, k) in memo:
                return memo[pos, k]
            if k == len(key):
                return 0
            lo = hi = pos
            himove = lomove = 0
            while get(hi) != key[k]:
                hi += 1
                himove += 1
            while get(lo) != key[k]:
                lo -= 1
                lomove += 1
            lo %= len(ring)
            hi %= len(ring)
            ans = min(dp(lo, k+1) + lomove, dp(hi, k+1) + himove) 
            memo[pos, k] = ans
            return ans

        return dp(0, 0) + len(key)

/**
 * @param {string} ring
 * @param {string} key
 * @return {number}
 */
var findRotateSteps = function(ring, key) {
    var map = new Map();
    var i, j, k;
    var prev = [0], cur, pos;

    for (i = 0; i < ring.length; i++) {
        if (!map.has(ring[i])) { map.set(ring[i], []); }
        map.get(ring[i]).push(i);
    }

    var last = [0];
    for (i = 0; i < key.length; i++) {
        cur = [];
        pos = map.get(key[i]);
        for (j = 0; j < pos.length; j++) {
            var min = Number.MAX_SAFE_INTEGER;
            for (k = 0; k < last.length; k++) {
                var diff = pos[j] - last[k];
                if (diff > 0) { diff = Math.min(diff, Math.abs(diff - ring.length)); }
                if (diff < 0) { diff = Math.min(-diff, diff + ring.length); }
                min = Math.min(min, prev[k] + diff);
            }
            cur[j] = min;
        }
        last = pos;
        prev = cur;
    }
    return Math.min(...prev) + key.length;
};

class Solution {
    public int countTriplets(int[] A) {
        int[] f = new int[1<<16];
        for(int v : A)f[v]++;
        for(int i = 0;i < 16;i++){
        	for(int j = 0;j < 1<<16;j++){
        		if(j<<~i>=0){
        			f[j] += f[j|1<<i];
        		}
        	}
        }
        for(int i = 0;i < 1<<16;i++){
        	f[i] = f[i] * f[i] * f[i];
        }
        for(int i = 0;i < 16;i++){
        	for(int j = 0;j < 1<<16;j++){
        		if(j<<~i>=0){
        			f[j] -= f[j|1<<i];
        		}
        	}
        }
        return f[0];
    }
}

class Solution {
public:
    int countTriplets(vector<int>& a) {
        int n = a.size();
        int s = 0;
        long long ans = n * n * n;
        for (int i = 1; i < (1 << 16); i++) {
            int now = i;
            int x = 1;
            while (now > 0) {
                now -= (now & -now);
                x *= -1;
            }

            int cur = 0;
            for (int j = 0; j < n; j++) {
                if ((a[j] & i) == i) {
                    cur++;
                }
            }

            ans += x * cur * cur * cur;
        }

        return ans;
    }
};

class Solution(object):
    def countTriplets(self, A):
        """
        :type A: List[int]
        :rtype: int
        """        
        bits = len(bin(max(A))) - 2
        bound = 1 << bits
        collect = [0] * bound
        for i in A:
            collect[i] += 1
        for j in range(bits):
            mask = 1 << j
            for i in range(bound):
                if i & mask == 0:
                    collect[i] += collect[i ^ mask]
        return sum((-1 if bin(i).count('1') % 2 else 1) * (collect[i] ** 3) for i in range(bound))

