var countTriplets = function(A) {
    const cnt = new Array(2 ** 16).fill(0);
    for (let i = 0; i < 2 ** 16; i++) {
        for (let j = 0; j < A.length; j++) {
            if ((i & A[j]) == 0) {
                cnt[i]++;
            }
        }
    }
    let res = 0;
    for (let i = 0; i < A.length; i++) {
        for (let j = 0; j < A.length; j++) {
            res += cnt[A[i] & A[j]];
        }
    }
    return res;
};

