int permutation(int i, int j, int k){
    int same = 0;
    if (i == j){
        if (j == k)
            same = 3;
        else
            same = 2;
    }
    else if (j == k){
        same = 2;   
    }
    if (same == 0){
        return 6;
    }
    else if (same == 2){
        return 3;
    }
    else{
        return 1;
    }
}
int countTriplets(int* A, int ASize) {
    int result = 0;
    for (int i = 0; i < ASize; i ++){
        for (int j = i; j < ASize;  j++){
            for (int k = j; k < ASize; k ++){
                if ((A[i] & A[j] & A[k]) == 0){
                    result += permutation(i, j, k);
                }
            }
        }
    }
    return result;
}

class Solution {
public:
    int M=1e9+7;
    int countRoutes(vector<int>& l, int start, int finish, int f) {
        int n=l.size();
        vector<vector<int>> dp(f+1, vector<int>(n));
        dp[0][start]=1;
        for(int i=0; i<f; ++i) {
            for(int j=0; j<n; ++j) {
                for(int k=0; k<n; ++k) {
                    if(k==j||i+abs(l[k]-l[j])>f)
                        continue;
                    dp[i+abs(l[k]-l[j])][k]=(dp[i+abs(l[k]-l[j])][k]+dp[i][j])%M;
                }
            }
        }
        int ans=0;
        for(int i=0; i<=f; ++i)
            ans=(ans+dp[i][finish])%M;
        return ans;
    }
};

class Solution {
    public int countRoutes(int[] locs, int start, int finish, int fuel) {
        int n = locs.length;
        int[][] dp = new int[fuel+1][n];
        dp[fuel][start] = 1;
        for (int i = fuel; i > 0; --i) {
            for (int j = 0; j < n; ++j) {
                if (dp[i][j]==0)
                    continue;
                for (int k = 0; k < n; ++k) {
                    if (j==k)
                        continue;
                    int nf = i-Math.abs(locs[j]-locs[k]);
                    if (nf<0)
                        continue;
                    dp[nf][k] += dp[i][j];
                    dp[nf][k] %= 1000000007;
                }
            }
        }
        int ans = 0;
        for (int i = 0; i <= fuel; ++i) {
            ans += dp[i][finish];
            ans %= 1000000007;
        }
        return ans;
    }
}

