class Solution(object):
    def countRoutes(self, locations, start, finish, fuel):
        """
        :type locations: List[int]
        :type start: int
        :type finish: int
        :type fuel: int
        :rtype: int
        """
        dp = {}
        n = len(locations)
        mod = 1000000007
        def dfs(c, s):
            if c==0 and s==finish: return 1
            k = (c, s)
            if k in dp: return dp[k]
            if s==finish:
                r=1
            else:
                r=0

            i = 0
            while i<n:
                if i!=s:
                    d = locations[i]-locations[s]
                    if d<0: d=-d
                    if c>=d:
                        r += dfs(c-d, i)
                        r %= mod
                i+=1
            dp[k] = r
            return r
        return dfs(fuel, start)

/**
 * @param {number[]} locations
 * @param {number} start
 * @param {number} finish
 * @param {number} fuel
 * @return {number}
 */
var countRoutes = function(locations, start, finish, fuel) {
    let memo = new Map()
    let N = locations.length
    let MOD = 1e9 + 7
    let ans = 0
    for (let i = 0; i <= fuel; i++) {
        ans += dfs(finish, i)
        ans %= MOD
    }
    // console.log(memo)
    return ans

    function dfs(city, fuel) {
        let key = city * 1000 + fuel
        if (!memo.has(key)) {
            memo.set(key, helper(city, fuel))
        }
        return memo.get(key)
    }
    function helper(city, fuel) {
        if (fuel === 0) {
            return city === start ? 1 : 0
        }
        if (fuel < 0) return 0

        let ans = 0
        for (let u = 0; u < N; u++) {
            if (u === city) continue
            ans += dfs(u, fuel - Math.abs(locations[u] - locations[city]))
            ans %= MOD
        }
        return ans
    }
};

class Solution {
    public int mincostTickets(int[] days, int[] costs) {
        int n = days.length;
        int[] dp = new int[n+1];
        Arrays.fill(dp, Integer.MAX_VALUE / 2);
        dp[0] = 0;
        for(int i = 1;i <= n;i++){
        	dp[i] = dp[i-1] + costs[0];
        	for(int j = i-1;j >= 0;j--){
        		if(days[i-1] - days[j] + 1 <= 7){
        			dp[i] = Math.min(dp[i], dp[j] + costs[1]);
        		}
        		if(days[i-1] - days[j] + 1 <= 30){
        			dp[i] = Math.min(dp[i], dp[j] + costs[2]);
        		}
        	}
        }
        return dp[n];
    }
}

