var mincostTickets = function(days, costs) {
    const dp = new Array(366).fill(0);
    const set = new Set(days);
    let last = 0;
    for (let i = 1; i < 366; i++) {
        if (!set.has(i)) {
            dp[i] = last;
        } else {
            // 1 day;
            dp[i] = dp[i - 1] + costs[0];
            for (let j = Math.max(i - 7, 0); j < i; j++) {
                dp[i] = Math.min(dp[i], dp[j] + costs[1]);
            }
            for (let j = Math.max(i - 30, 0); j < i; j++) {
                dp[i] = Math.min(dp[i], dp[j] + costs[2]);
            }
            last = dp[i];
        }
    }
    return last;
};

from functools import lru_cache

class Solution:
    def mincostTickets(self, days: 'List[int]', costs: 'List[int]') -> 'int':
        @lru_cache(None)
        def dp(i):
            if i >= len(days): return 0
            current = days[i]
            seven = i
            while seven < len(days) and days[seven] < current+7: seven += 1
            thirty = seven
            while thirty < len(days) and days[thirty] < current+30: thirty += 1
            x = min([dp(i+1) + costs[0], dp(seven) + costs[1], dp(thirty) + costs[2]])
            return x
        return dp(0)

int mincostTicketsH(int* days, int daysSize, int* costs, int costsSize, int* mem);
int forward(int *days, int daysSize, int daysForward){
    int i = 1;
    while (i < daysSize){
        if (days[i] - days[0] < daysForward){
            i ++;
        }
        else{
            return i;
        }
    }
    return i;
}
int mincostTickets(int* days, int daysSize, int* costs, int costsSize){
    int* mem = (int*)malloc(366 * sizeof(int));
    for (int i = 0; i < 366; i ++)
        mem[i] = -1;
    return mincostTicketsH(days, daysSize, costs, costsSize, mem);
}

int mincostTicketsH(int* days, int daysSize, int* costs, int costsSize, int* mem) {
    if (mem[daysSize] != -1)
        return mem[daysSize];
    if (daysSize == 0)
        return 0;
    if (daysSize == 1)
        return costs[0];
    else{
        int oneD = costs[0] + mincostTicketsH(days + 1, daysSize - 1, costs, costsSize, mem);
        int forwardS = forward(days, daysSize, 7);
        int sevenD = costs[1] + mincostTicketsH(days + forwardS, daysSize - forwardS, costs, costsSize, mem);
        int forwardT = forward(days, daysSize, 30);
        int thirtyD = costs[2] + mincostTicketsH(days + forwardT, daysSize - forwardT, costs, costsSize, mem);
        int m = oneD < sevenD ? oneD : sevenD;
        int r = m < thirtyD ? m : thirtyD;
        mem[daysSize] = r;
        return r;
    }
}
